def myMapFuncParseInt(binString):
    return int(binString,2)

### Check if the seed words form a valid checksum. All ICO wallets have a valid seed checksum. 
def isValidChecksumForMnemonic(seedWords, wordList):
    myWords = seedWords.split(' ')
    myWordsLen = len(myWords)
    myWordPosBinString = ""
    #I have already verified that all words are in the list
    if (len(myWords) % 3 != 0):
        sys.stderr.write("[WARNING] Seed Words Must be a multple of 3!\n")
        return False
    for t in myWords:
        myWordPosBinString += str(bin(wordList.index(t)))[2:].rjust(11, "0");

    x = 32 * math.floor(len(myWordPosBinString) / 33)
    seedWordChecksum=myWordPosBinString[x:] #from x to end

    myIntList = list(map(myMapFuncParseInt,re.findall('.{1,8}', myWordPosBinString[0:x])))
    z = hashlib.sha256(bytes(myIntList)).digest()
    info = [str(bin(int.from_bytes(hashlib.sha256(bytes(myIntList)).digest()[i:i+1], byteorder='big')))[2:].rjust(8, "0") for i in range(0, len(z), 1)]
    generatedChecksum = str(info[0])[0:int((8 * len(myIntList)) / 32)]

    if generatedChecksum != seedWordChecksum:
        sys.stderr.write("[WARNING] Invalid mnemonic checksum! Check your seed words!\n")
        return False
    return True




