/*
 * Common code for cracking DiskCryptor.
 *
 * This file takes replicated but common code, shared between the CPU
 * and the GPU formats, and places it into one common location.
 */

#include "arch.h"
#include "memory.h"
#include "jumbo.h"
#include "johnswap.h"
#include "xts.h"
#include "diskcryptor_common.h"

// #define DEBUG 1

